# [cite_start]Extracted from markers.tab [cite: 7, 11, 15, 19, 22, 26, 30, 34, 38, 42]
ENZYME_DB = {
    "EcoRI": "GAATTC",
    "BamHI": "GGATCC",
    "HindIII": "AAGCTT",
    "PstI": "CTGCAG",
    "KpnI": "GGTACC",
    "SacI": "GAGCTC",
    "SalI": "GTCGAC",
    "XbaI": "TCTAGA",
    "NotI": "GCGGCCGC",
    "SmaI": "CCCGGG",
    "SphI": "GCATGC"  # Common lab standard
}

# [cite_start]Mapping Design names to specific marker roles [cite: 54, 68]
# Sequences are representations; in production, these would be full gene sequences
MARKER_DB = {
    "Ampicillin": "",
    "Blue_White_Selection": "",
    "High_Copy_Replication": ""
}

def get_marker_sequence(name):
    return MARKER_DB.get(name)

def get_enzyme_sequence(name):
    return ENZYME_DB.get(name)